import './App.css';
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as yup from "yup";

function App() {
  const handleClickLogin = (values) => console.log(values);

  const validationLogin = yup.object().shape({
    email: yup.string().email("Não é um email").required("O email é obrigatório"),
    password: yup.string().min(8, "Senha inválida").required("A senha é obrigatória"),
  });

  const handleClickCadastro = (values) => console.log(values);

  const validationCadastro = yup.object().shape({
    email: yup.string().email("Não é um email").required("O email é obrigatório"),
    password: yup.string().min(8, "Senha inválida").required("A senha é obrigatória"),
    confirmPassword: yup.string().oneOf([yup.ref("password"), null], "Senhas incompatíveis"),
  });

  return (
    <div className="container">
      <h1>Login</h1>
      <Formik
        initialValues={{}}
        onSubmit={handleClickLogin}
        validationSchema={validationLogin}
      >
        <Form className="login-form">
          <div className="login-form-grupo">
            <Field name="email" className="form-field" placeholder="Email" />

            <ErrorMessage component="span" name="email" className="form-erro" />
          </div>

          <div className="login-form-grupo">
            <Field name="password" className="form-field" placeholder="Senha" />

            <ErrorMessage component="span" name="password" className="form-erro" />
          </div>

          <button className="button" type="submit">Entrar</button>
        </Form>
      </Formik>

      <h1>Cadastro</h1>
      <Formik
        initialValues={{}}
        onSubmit={handleClickCadastro}
        validationSchema={validationCadastro}
      >
        <Form className="cadastro-form">
          <div className="cadastro-form-grupo">
            <Field name="email" className="form-field" placeholder="Email" />

            <ErrorMessage component="span" name="email" className="form-erro" />
          </div>

          <div className="cadastro-form-grupo">
            <Field name="password" className="form-field" placeholder="Senha" />

            <ErrorMessage component="span" name="password" className="form-erro" />
          </div>

          <div className="cadastro-form-grupo">
            <Field name="confirmPassword" className="form-field" placeholder="Confirme sua senha" />

            <ErrorMessage component="span" name="confirmPassword" className="form-erro" />
          </div>

          <button className="button" type="submit">Cadastrar</button>
        </Form>
      </Formik>
    </div>
  );
}

export default App;
